import { MessageBox } from 'react-chat-elements';
import { ChatItem, MessageList, ChatList } from 'react-chat-elements'
import React, {useState, useEffect} from 'react';
import axios from 'axios';

function Chat() {
    const [chats, setChats] = useState([])

    useEffect(() => {
        axios.get('http://10.110.160.50:8008/api/chat?userId=29').then(res => setChats(
            res.data.map((item) =>  {
             return   {
                    message: item.title
                }
        })
        )).catch(err => err)
    }, [])
    return(
        <ChatList
    className='chat-list'
    dataSource={chats} />
    )
}

export default Chat;
