import { MessageBox } from 'react-chat-elements';
import { ChatItem, MessageList, ChatList } from 'react-chat-elements'
import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {withRouter} from 'react-router-dom'

function Chat(props) {
    const [chats, setChats] = useState([])
    const handleClick = e => {
        props.history.push(`/message/${e.chattingUserId}`)
    }
    useEffect(() => {
        axios.get('http://10.110.160.50:8008/api/chat?userId=29').then(res => setChats(
            res.data.map((item) =>  {
             return  {
                    title: item.message,
                    chattingUserId: item.chattingUserId
                }
        })
        )).catch(err => err)
    }, [])
    return(
        <ChatList
        onClick={handleClick}
    className='chat-list'
    dataSource={chats} />
    )
}

export default (withRouter(Chat));
